import React, { useState, useEffect } from 'react';

const Buscador = () => {
    const [user, setUser] = useState([])
    const [search, setSearch] = useState("")
    const URL = 'https://jsonplaceholder.typicode.com/users'

    const showData = async () => {
        const response = await fetch(URL)
        const data = await response.json()
        setUser(data)
    }

    const searcher = (e) => {
        setSearch(e.target.value)
    }

   const results = !search ? user : user.filter((dato) => dato.name.toLowercase().includes(search.toLocaleLowerCase()))

useEffect(() => {
    showData()
}, [])

return (
    <div>


        <input value={search} onChange={searcher} type="text" placeholder="Buscar colaborador" className='form-control' />

        <table className='table table-striped table-hover mt-5 shadow-lg'>
            <thead>
                <tr key={user.id} >
                    <th>Nombre</th>
                    <th>Correo</th>
                </tr>
            </thead>
            <tbody>
                {user.map((user) =>
                    <tr>
                        <td>{user.name}</td>
                        <td>{user.email}</td>
                    </tr>)}
            </tbody>
        </table>

    </div>
);
}

export default Buscador;
